ActorFrame.InitCommand:
%function(self)
RzCheckMetalClassicTaro = 0;
self:z(0);
if RGenerationA == nil then RGenerationA = 0; end
if RGenerationA == 2 then RGenerationA = 0;	end
if RGenerationA == 0 then
	self:z(1);
	RGenerationA = RGenerationA + 1;
	else
	RGenerationA = RGenerationA + 1;
	end
end

ActorFrame.PressCommand:
%function(self)
RzCheckMetalClassicTaro = self:GetZ();
if RzCheckMetalClassicTaro > 0 then
	if GAMESTATE:IsPlayerEnabled(PLAYER_1) then
		RpressP1 = 1;
		else
		RpressP2 = 1;
		end
else
	RpressP2 = 1;
end
end

ActorFrame.LiftCommand:
%function(self)
RzCheckMetalClassicTaro = self:GetZ();
if RzCheckMetalClassicTaro > 0 then
	if GAMESTATE:IsPlayerEnabled(PLAYER_1) then
		RpressP1 = 0;
		else
		RpressP2 = 0;
		end
else
	RpressP2 = 0;
end
end
