ActorFrame.InitCommand:
%function(self)
DzCheckMetalClassicTaro = 0;
self:z(0);
if DGenerationA == nil then DGenerationA = 0; end
if DGenerationA == 2 then DGenerationA = 0;	end
if DGenerationA == 0 then
	self:z(1);
	DGenerationA = DGenerationA + 1;
	else
	DGenerationA = DGenerationA + 1;
	end
end

ActorFrame.PressCommand:
%function(self)
DzCheckMetalClassicTaro = self:GetZ();
if DzCheckMetalClassicTaro > 0 then
	if GAMESTATE:IsPlayerEnabled(PLAYER_1) then
		DpressP1 = 1;
		else
		DpressP2 = 1;
		end
else
	DpressP2 = 1;
end
end

ActorFrame.LiftCommand:
%function(self)
DzCheckMetalClassicTaro = self:GetZ();
if DzCheckMetalClassicTaro > 0 then
	if GAMESTATE:IsPlayerEnabled(PLAYER_1) then
		DpressP1 = 0;
		else
		DpressP2 = 0;
		end
else
	DpressP2 = 0;
end
end
