ActorFrame.InitCommand:
%function(self)
LzCheckMetalClassicTaro = 0;
self:z(0);
if LGenerationA == nil then LGenerationA = 0; end
if LGenerationA == 2 then LGenerationA = 0; end
if LGenerationA == 0 then
	self:z(1);
	LGenerationA = LGenerationA + 1;
	else
	LGenerationA = LGenerationA + 1;
	end
end

ActorFrame.PressCommand:
%function(self)
LzCheckMetalClassicTaro = self:GetZ();
if LzCheckMetalClassicTaro > 0 then
	if GAMESTATE:IsPlayerEnabled(PLAYER_1) then
		LpressP1 = 1;
		else
		LpressP2 = 1;
		end
else
	LpressP2 = 1;
end
end

ActorFrame.LiftCommand:
%function(self)
LzCheckMetalClassicTaro = self:GetZ();
if LzCheckMetalClassicTaro > 0 then
	if GAMESTATE:IsPlayerEnabled(PLAYER_1) then
		LpressP1 = 0;
		else
		LpressP2 = 0;
		end
else
	LpressP2 = 0;
end
end
